              <button
                className="pick-theme-button"
                onClick={handleClickPopover}
                style={{ marginLeft: "20px" }}
              >
                Pick Theme
              </button>
              <Popover
                id={idPopover}
                open={openPopover}
                anchorEl={anchorEl}
                transformOrigin={{ vertical: "top", horizontal: "left" }}
                onClose={handleClosePopover}
              >
                <Card sx={{ minWidth: 275}}>
                  <CardContent>
                    <div>
                      <div>
                        <h3 style={{ display: "flex", marginTop: "4px" }}>
                          Palette
                        </h3>
                        <div style={{ display: "flex", marginTop: "4px" }}>
                          {themes.map((theme, index) => (
                            <div
                              key={index}
                              className={
                                index === activeColor
                                  ? "combo colors-active colors"
                                  : "combo colors"
                              }
                              onClick={(e) => chooseColor(theme, index)}
                              style={{
                                background: `linear-gradient(45deg, ${theme[0]}, ${theme[1]})`,
                              }}
                            ></div>
                          ))}
                        </div>
                      </div>
                      <div>
                        <h3 style={{ marginTop: "4px" }}>Create New</h3>
                        <div
                          className="colors"
                          style={{
                            background: `linear-gradient(45deg, ${picker1Color}, ${picker2Color})`,
                          }}
                        ></div>
                        <div>
                          Primary{" "}
                          <ColorPicker
                            defaultValue={picker1Color}
                            value={picker1Color}
                            onChange={(color) =>
                              setPicker1Color(`#${color.hex}`)
                            }
                            deferred
                            disableAlpha
                          />
                          Secondary{" "}
                          <ColorPicker
                            defaultValue={picker2Color}
                            value={picker2Color}
                            onChange={(color) =>
                              setPicker2Color(`#${color.hex}`)
                            }
                            deferred
                            disableAlpha
                          />
                        </div>
                        <div className="fontpicker-div">
                        <div className="fontpicker-label">Font</div>
                          <Select
                            value={fontFamily}
                            variant="standard"
                            onChange={(e) => setFontFamily(e.target.value)}
                            className="fontpicker"
                            autoWidth={true}
                          >
                            {fonts.map((font, idx) => (
                              <MenuItem value={font} key={idx}>
                                {font}
                              </MenuItem>
                            ))}
                          </Select>
                        </div>
                        <button
                          style={{ marginTop: "20px" }}
                          onClick={saveTheme}
                          className="save-theme-button"
                        >
                          Save Theme
                        </button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </Popover>
            </div>
          </div>
          <div className="resume-preview">
            <ComponentToPrint
              photourl={photourl}
              name={name}
              subtitle={subtitle}
              userdesc={userdesc}
