              project3={project3}
              project4={project4}
              primary={primary}
              secondary={secondary}
              ref={componentRef}
              fontFamily={fontFamily}
            />
          </div>
        </div>
      </main>
      <footer className="footer">
        <p>Copyright © 2021. All rights reserved.</p>
      </footer>
    </div>
  );
}

